const Footer = () => {
  return (
    <footer className="p-8 pb-4 flex flex-col gap-8 items-center">
      <ul className="links flex divide-x-2 divide-current">
        {links.map((link, index) => (
          <motion.li
            key={link.name + index}
            className="relative px-4"
            whileHover="show"
            initial="hide"
          >
            <a href={link.href}>{link.name}</a>
            <motion.div
              variants={{ show: { scaleX: 0.7 }, hide: { scaleX: 0 } }}
              className="absolute top-full left-0 w-full h-0.5 bg-current"
            ></motion.div>
          </motion.li>
        ))}
      </ul>

      <ul className="social-links flex gap-4">
        {socialLinks.map((link) => (
          <motion.li key={link.name + link.href} whileHover={{ y: -3 }} layout>
            {!link.hidden && (
              <a
                href={link.href}
                className="flex justify-center items-center rounded-full size-11 bg-neutral-200 text-[#111D4D]"
                target="_blank"
                rel="noreferrer"
              >
                {link.icon}
              </a>
            )}
          </motion.li>
        ))}
      </ul>

      <p>
        &copy; 2024 Pineapp solutions limited, a startup with Enugu State
        Technology Hub{" "}
      </p>
    </footer>
  );
};

export default Footer;
